import React, { useState } from 'react';
import { StatusBar } from 'react-native';
import Icon from '@expo/vector-icons/MaterialIcons'

import { Container, Content, Back, Img, Image, ViewSearch, Input, Title, Button, SlideView } from './src/styles/Styles'
import movies from './src/assets/movies.js'


export default function MovieApp() {

  const [lista, setLista] = useState(movies);

  const [background, setBackground] = useState(lista[0].img)

  const handleTest = () => {
    alert(background)
  }
  return (

    <Container>
      <StatusBar barStyle="light-content" backgroundColor="transparent" translucent />
          <Image 
            source={{ uri: background}}
            blurRadius={8}
            />
        <Back >
          <Img
            source={{ uri: background}}
            blurRadius={8}
            >
            <ViewSearch>
              <Input 
                placeholder="Procurando algo"
                />
              <Button>
                <Icon name="search" color="#000" size={25} />
              </Button>
            </ViewSearch>

            <Title>Acabou de chegar</Title>
            <Button title="teste" onPress={handleTest} />
          </Img>
        </Back>
      <Content>
      </Content>
    </Container>
  );
}
